<?php

namespace AkademiaBundle\Repository;

/**
 * ApoderadoRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class ApoderadoRepository extends \Doctrine\ORM\EntityRepository
{
	public function getbuscarApoderado($dni){

        $query ="select id from ACADEMIA.apoderado where dni='$dni' and estado=1";
        $stmt = $this->getEntityManager()->getConnection()->prepare($query);
        $stmt->execute();
        $dni = $stmt->fetchAll();

    	return $dni;
	}

    public function getbuscarNuevoApoderado($dni,$id){

        $query ="select id from ACADEMIA.apoderado where dni='$dni' and id=$id";
        $stmt = $this->getEntityManager()->getConnection()->prepare($query);
        $stmt->execute();
        $dni = $stmt->fetchAll();

        return $dni;
    }

	public function getApoderadoBusqueda($dni){

        $query ="select top 1 id from ACADEMIA.apoderado where dni='$dni' and estado = 0";
        $stmt = $this->getEntityManager()->getConnection()->prepare($query);
        $stmt->execute();
        $dni = $stmt->fetchAll();

    	return $dni;
	}



}
